        /// </summary>
        /// <param name="velMode"></param>
        /// <param name="dir">true:正转  false:反转</param>
        /// <returns></returns>
        private short ConveyorJog(bool dir)
        {
            ConveyorIsRun = true;
            ConveyorRunDir = dir;
            short rtn = 0;
            if (VariableSys.machineVersion == 3)
            {
                rtn += R3.ResetIO_OUT(5);
                rtn += R3.ResetIO_OUT(4);
                if (dir)
                {
                    rtn += R3.SetIO_OUT(4);
                }
                else
                {
                    rtn += R3.SetIO_OUT(5);
                    rtn += R3.SetIO_OUT(4);
                }
            }
            else
            {
                rtn += R2.ResetIO_OUT(5);
                rtn += R2.ResetIO_OUT(4);

                if (dir)
                {
                    rtn += R2.SetIO_OUT(4);
                }
                else
                {
                    rtn += R2.SetIO_OUT(5);
                    rtn += R2.SetIO_OUT(4);
                }
            }
            return rtn;
        }

