            else
            {
                Variable.sPath_SYS_MachineNO = programPath;
                Variable.sPath_SYS_Program = Variable.sPath_SYS_MachineNO + "\\ALMProgram";//打包程式
                Variable.sPath_SYS_FEEDER = Variable.sPath_SYS_MachineNO + "\\FEEDER";//程式模块1-Feeder
