        private PointF[] TransformPointsForm1Mark(PointF[] POINTS, PointF Mark1, PointF newMark1)
        {
            PointF[] ReturnPoints = new PointF[POINTS.Length];
            for (int i = 0; i < POINTS.Length; i++)
