                Ini_Sys.IniWriteNumber("Position", string.Format("ThrowPoint{0}_1Y", i + 1), zParam.ThrowPoint1.Y);

                Ini_Sys.IniWriteNumber("Position", string.Format("Init_R{0}_Deg", i + 1), this.R_RunParamMap[i].InitPos);
