                Ini_Sys.IniWriteNumber("Position", $"CamHeightZ{i + 1}", this.Z_RunParamMap[i].CamHeigh);
                Ini_Sys.IniWriteNumber("Position", $"PasteHeightZ{i + 1}", this.Z_RunParamMap[i].PasteHeight);
