                if (!X.bAxisIsRunning || !Y.bAxisIsRunning)
                    XYGoPos(VariableSys.pReadyPoint, VariableSys.VelMode_Current);

                if (!Turn.bAxisIsRunning)
                    this.TurnGo(VariableSys.dTurnPasteAngle, VariableSys.VelMode_Current);
            }
            return true;
