                if (RUN_PASTEInfo[runPasteIndex].BadMark_AlgthrimIndex == 1)//面积算法
                {
                    bool AreaWhite = false;
                    if (RUN_PASTEInfo[runPasteIndex].ISBadMarkWhite == 0 || RUN_PASTEInfo[runPasteIndex].ISBadMarkWhite == 2)
                    {
                        AreaWhite = true;
                    }
                    else
