            {
                Image.Overlays.Default.AddRectangle(new RectangleContour(LabelInfo.Init_ROI_Left2, LabelInfo.Init_ROI_Top2, LabelInfo.Init_ROI_Width2, LabelInfo.Init_ROI_Height2));
